mapper.select:
select b.id,b.token_name,b.token_short_name,b.img_path,b.expected_venue,b.issue_amount,b.allowance,b.issue_price,b.issue_time,b.total_house_number,b.rent_out_number,b.sell_number,b.using_flag,b.contract_account,b.issue_company,b.token_identity,b.tx_rule,b.asset_details,b.company_info,b.issue_company_en,b.token_identity_en,b.tx_rule_en,b.asset_details_en,b.company_info_en,b.create_date,b.create_user,b.update_date,b.update_user
from tz_invest_item b
where b.id =#{id}

select b.id,b.token_name,b.token_short_name,b.img_path,b.expected_venue,b.issue_amount,b.allowance,b.issue_price,b.issue_time,b.total_house_number,b.rent_out_number,b.sell_number,b.using_flag,b.contract_account,b.issue_company,b.token_identity,b.tx_rule,b.asset_details,b.company_info,b.issue_company_en,b.token_identity_en,b.tx_rule_en,b.asset_details_en,b.company_info_en,b.create_date,b.create_user,b.update_date,b.update_user
from tz_invest_item b


        and b.id= #{id}


        and b.token_name= #{tokenName}


        and b.token_short_name= #{tokenShortName}


        and b.img_path= #{imgPath}


        and b.expected_venue= #{expectedVenue}


        and b.issue_amount= #{issueAmount}


        and b.allowance= #{allowance}


        and b.issue_price= #{issuePrice}


        and b.issue_time= #{issueTime}


        and b.issue_time  >= #{issueTimeBegin}


        and b.issue_time  <= #{issueTimeEnd}


        and b.total_house_number= #{totalHouseNumber}


        and b.rent_out_number= #{rentOutNumber}


        and b.sell_number= #{sellNumber}


        and b.using_flag= #{usingFlag}


        and b.contract_account= #{contractAccount}


        and b.issue_company= #{issueCompany}


        and b.token_identity= #{tokenIdentity}


        and b.tx_rule= #{txRule}


        and b.asset_details= #{assetDetails}


        and b.company_info= #{companyInfo}


        and b.issue_company_en= #{issueCompanyEn}


        and b.token_identity_en= #{tokenIdentityEn}


        and b.tx_rule_en= #{txRuleEn}


        and b.asset_details_en= #{assetDetailsEn}


        and b.company_info_en= #{companyInfoEn}




        ${order}

select b.id,b.token_name,b.token_short_name,b.img_path,b.expected_venue,b.issue_amount,b.allowance,b.issue_price,b.issue_time,b.total_house_number,b.rent_out_number,b.sell_number,b.using_flag,b.contract_account,b.issue_company,b.token_identity,b.tx_rule,b.asset_details,b.company_info,b.issue_company_en,b.token_identity_en,b.tx_rule_en,b.asset_details_en,b.company_info_en,b.create_date,b.create_user,b.update_date,b.update_user
from tz_invest_item b


      and b.id= #{id}


      and b.token_name= #{tokenName}


      and b.token_short_name= #{tokenShortName}


      and b.img_path= #{imgPath}


      and b.expected_venue= #{expectedVenue}


      and b.issue_amount= #{issueAmount}


      and b.allowance= #{allowance}


      and b.issue_price= #{issuePrice}


      and b.issue_time= #{issueTime}


        and b.issue_time  >= #{issueTimeBegin}


        and b.issue_time  <= #{issueTimeEnd}


      and b.total_house_number= #{totalHouseNumber}


      and b.rent_out_number= #{rentOutNumber}


      and b.sell_number= #{sellNumber}


      and b.using_flag= #{usingFlag}


      and b.contract_account= #{contractAccount}


      and b.issue_company= #{issueCompany}


      and b.token_identity= #{tokenIdentity}


      and b.tx_rule= #{txRule}


      and b.asset_details= #{assetDetails}


      and b.company_info= #{companyInfo}


      and b.issue_company_en= #{issueCompanyEn}


      and b.token_identity_en= #{tokenIdentityEn}


      and b.tx_rule_en= #{txRuleEn}


      and b.asset_details_en= #{assetDetailsEn}


      and b.company_info_en= #{companyInfoEn}




